import styled from "styled-components";
import Computer from "../../Assets/Imagem2.webp"

export const BoxContainer = styled.section`
    border: solid red;
    background-image: url(${Computer});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    object-fit: contain;
    height: 100vh;
    width: 100%;

    ul{
        border: solid red;
        display: flex;


    }

    li{
        border: solid green;
        font-size: 1.5rem;
        list-style: none;
    }
`

export const Title = styled.h1`
    border: solid pink;
    font-size: 4rem;
    text-align: center;
    text-shadow: 5px 5px ;
    width: 100%;

`

export const Footer = styled.div`
    border: solid green;
    text-align: center;
`
