import styled from "styled-components";
import Background from "../../Assets/Background.1.jpg"

export const Container = styled.section`
    background-image: url(${Background});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;

    h1{
        color: whitesmoke;
        font-size: 3rem;
        letter-spacing: 1rem;
        text-align: center;
        text-shadow: 5px 5px 10px wheat;
        width: 100%;
        margin-bottom: 2rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column;

        h1{
            font-size: 1.5rem;
        }
    }
`
export const Home = styled.div`
    height: 3rem;
    padding: 1rem;
    width: 100%;
    
    a{
        color: whitesmoke;
        text-decoration: none;
        font-size: 1.5rem;
        list-style: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }
    
    @media only screen and (min-width: 360px) and (max-width: 800px){
        text-align: center;
    }
`
export const Box_Hobbies = styled.section`
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-around;

    div{
        margin-top: 2rem;
        display: flex;
        flex-direction: column;
        align-items: center;
        height: 25rem;
        width: 15rem;
    }

    img{
        width: 10rem;
        transition: 1s all;

        &:hover{
            transform: scale(1.2);
        }
    }

    p{
        color: wheat;
        font-size: 1.3rem;
        text-align: center;
        margin-top: 1rem;
        width: 100%;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column;
        justify-content: space-evenly;
        align-items: center;
        border: solid green;
        
        div{
            border: solid white;
            flex-direction: row;
            
            width: 24rem;
            height: 6rem;
        }

        img{
            width: 6rem;
        }

        p{
            border: solid yellow;
            
            
            font-size: 1rem;
            height: 6rem;
        }
    }
`
