import styled from "styled-components";
import Lua from "../../Assets/Eunalua.1.jpg"

export const Container = styled.body`
    background-image: url(${Lua});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;

    h5{
        font-size: 1rem;
        text-align: center;
        text-shadow: 5px 5px 20px white;
        height: 2rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        background-position: 35%;

        h5{
            height: 4rem;
            margin-top: 1rem;
        }
    }
`

export const Box = styled.div`
    display: flex;
    flex-direction: column;
    height: 95vh;
    width: 100%;
    
    ul{
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        margin-top: 1rem;
        width: 100%;
    }

    li{
        font-size: 1.3rem;
        list-style: none;
    }

    a{
        color: whitesmoke;
        text-decoration: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column-reverse;

        ul{
            flex-direction: column;
            align-items: flex-end;
            height: 13rem;
            padding-right: 1rem;
        }

        a{
            color: black;
        }
    }
`

export const Title = styled.div`
    /* margin-top: 3rem;
    margin-left: 32rem;
    width: 40rem; */
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width: 100%;
    height: 10rem;
    border: solid white;

    p{
        
        white-space: nowrap;
        overflow: hidden;

        /* width: 40rem; */
        color: whitesmoke;
        font-size: 2.5rem;
        text-align: center;
        text-shadow: 5px 5px 20px wheat;
        /* transition: 1s all;

       &:hover{
            transform: scale(1.1);
            cursor: pointer;
        } */
    }

    .line{
        
    }

    .typing-animation{
            animation: blinkCursor 500ms steps(40) infinite normal, 
            typing 4s steps(40) 1s normal both;
    }

    @keyframes typing{
        from{
            width: 0;
        }
        to{
            width: 18em;
        }
    }

    @keyframes blinkCursor{
            from{
                border-right-color: wheat;
            }
            to{
                border-right-color: transparent;
            }
        }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        width: 100%;
        margin-bottom: 10rem;
        margin-top: 0;
        margin-left: 0;
        
        p{
            width: 100%;
            font-size: 2rem;
            line-height: 3rem;
        }
    }
`
