import styled from "styled-components";
import Background from "../../Assets/Background.1.jpg"

export const Container = styled.section`
    background-image: url(${Background});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
`

export const Home = styled.div`
    height: 3rem;
    padding: 1rem;
    width: 100%;
    
    a{
        color: whitesmoke;
        text-decoration: none;
        font-size: 1.5rem;
        list-style: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }
    
    @media only screen and (min-width: 360px) and (max-width: 800px){
        text-align: center;
    }
`
export const Box = styled.section`
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 100vh;
    width: 100%;

    h1{
        color: whitesmoke;
        font-size: 3rem;
        letter-spacing: 1rem;
        text-align: center;
        text-shadow: 5px 5px 10px wheat;
        width: 100%;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column;

        h1{
            font-size: 2rem;
        }
    }
`

export const Section = styled.section`
    display: flex;
    flex-direction: column;
    width: 100%;

    div{
        margin: 0 auto;
    }

    img{
        width: 13rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        div{
            margin-bottom: 1rem;
        }

        img{
            width: 9rem;
    }
}
`
export const Tecnologies = styled.div`
    width: 100%;
    height: 45vh;
    display: flex;
    flex-direction: row;
    align-items: center;

    figure{
        display: flex;
        justify-content: center;
        flex-wrap: wrap;
        width: 100%;
    }

    img{
        width: 8rem;
        margin: 2rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){

        img{
            width: 6rem;
            margin: 1rem;
        }
    }
`
