import styled from "styled-components";
import Background from "../../Assets/Background.1.jpg"

export const Container = styled.section`
    background-image: url(${Background});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
`

export const Home = styled.div`
    height: 3rem;
    padding: 1rem;
    width: 100%;
    
    a{
        color: whitesmoke;
        text-decoration: none;
        font-size: 1.5rem;
        list-style: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }
    
    @media only screen and (min-width: 360px) and (max-width: 800px){
        text-align: center;
    }
`

export const AboutMe = styled.div`
    border: solid red;
    display: flex;
    flex-direction: column;
    height: 90vh;
    width: 100%;

    h1{
        border: solid green;
        color: whitesmoke;
        font-size: 3rem;
        letter-spacing: 1rem;
        margin-bottom: 1rem;
        text-align: center;
        text-shadow: 5px 5px 10px wheat;
        width: 100%;
    }

    div{
        display: flex;
        flex-direction: row;
    }

    img{
        width: 20rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        margin: 0;

        div{
            display: block;
        }

        h1{
            font-size: 2rem;
            margin-bottom: 1rem;
            margin-top: 1rem;
        }

        img{
            float: left;
            width: 9rem;  
        }
    }
`
export const History = styled.p`
border: solid yellow;
    color: wheat;
    font-size: 1.5rem;
    line-height: 2.5rem;
    

    @media only screen and (min-width: 360px) and (max-width: 800px){
        font-size: 1.2rem;
        line-height: 2rem;
        
    }
`
