import styled from "styled-components";
import Background from "../../Assets/Background.1.jpg"

export const Container = styled.section`
    background-image: url(${Background});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;

    h1{
        color: whitesmoke;
        font-size: 3rem;
        letter-spacing: 1rem;
        text-align: center;
        text-shadow: 5px 5px 10px wheat;
        width: 100%;
        margin-bottom: 2rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column;

        h1{
            font-size: 2rem;
        }
    }
`
export const Home = styled.div`
    height: 3rem;
    padding: 1rem;
    width: 100%;
    
    a{
        color: whitesmoke;
        text-decoration: none;
        font-size: 1.5rem;
        list-style: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }
    
    @media only screen and (min-width: 360px) and (max-width: 800px){
        text-align: center;
    }
`
export const Box_AcademicFormation = styled.section`
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    padding: 1rem;

    div{
        border: solid pink;
        width: 15rem;
        height: 15rem;
        display: flex;
        flex-direction: column;
        align-items: center;
    }

    img{
        width: 10rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-wrap: wrap;
        padding: 0;

        div{
            height: 5rem;
            margin-bottom: 2rem;
            flex-direction: row;
            align-items: flex-start;
            width: 100%;
        }

        img{
            width: 7rem;
            border: solid greenyellow;
        }
    }
`
export const Paragrafo = styled.p`
    border: solid yellow;
    width: 100%;
    height: 10rem;

    p{
        color: wheat;
        font-size: 1.2rem;
        text-align: center;
        margin-top: 1rem;
        width: 100%;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        height: 7rem;

        p{
            font-size: 1rem;
        }
    }         
`
