import styled from "styled-components";
import Background from "../../Assets/Background.1.jpg"

export const Container = styled.section`
    background-image: url(${Background});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
`

export const Home = styled.div`
    height: 3rem;
    padding: 1rem;
    width: 100%;
    
    a{
        color: whitesmoke;
        text-decoration: none;
        font-size: 1.5rem;
        list-style: none;

        &:hover{
            border-bottom: 5px dotted;
        }
    }
`

export const Box = styled.section`
    display: flex;
    flex-direction: row;
    align-items: center;
    height: 100vh;
    width: 100%;

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column;
    }
`

export const About = styled.div`
    display: flex;
    flex-direction: column;
    margin: 3rem;
    width: 100%;

    h1{
        color: whitesmoke;
        font-size: 3rem;
        letter-spacing: 1rem;
        margin-bottom: 2rem;
        text-align: center;
        text-shadow: 5px 5px 10px wheat;
        width: 100%;
    }

    div{
        display: flex;
        flex-direction: row;
    }

    img{
        width: 20rem;
        margin-left: 2rem;
    }

    p{
        color: wheat;
        font-family: 'Montserrat Alternates', sans-serif;
        font-size: 1.2rem;
        line-height: 2.5rem;
        margin-bottom: 1rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        margin: 0;

        div{
            flex-direction: column-reverse;
            
        }

        h1{
            font-size: 2rem;
            margin-bottom: 1rem;
            margin-top: 1rem;
        }

        img{
            width: 8rem;
            margin-left: 13rem;
            
        }

        p{
            font-size: 1rem;
            line-height: 2;
            margin-left: 1rem;
            margin-right: 1rem;
        }
    }
`
