import styled from "styled-components";

export const Container = styled.section`
    border: solid 5px pink;
    display: flex;

    height: 100vh;
    width: 100%;
`

export const About = styled.div`
    border: solid red;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;

    h1{
        border: solid green;
        font-size: 3rem;
        margin-bottom: 1rem;
        text-align: center;
        width: 100%;
    }

    p{
        border: solid yellowgreen;
        font-size: 1.2rem;
        width: 100%;
    }
`
