import styled from "styled-components";
import Lua from "../../Assets/Eunalua.1.jpg"

export const Container = styled.section`
    background-image: url(${Lua});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;

    h5{
        font-size: 1rem;
        text-align: center;
        text-shadow: 5px 5px 20px white;
        height: 2rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        background-position: 35%;

        h5{
            height: 4rem;
            margin-top: 1rem;
        }
    }
`

export const Box = styled.div`
    display: flex;
    flex-direction: column;
    height: 95vh;
    width: 100%;
    
    ul{
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        margin-top: 1rem;
        width: 100%;
    }

    li{
        font-size: 1.5rem;
        list-style: none;
    }

    a{
        color: whitesmoke;
        text-decoration: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column-reverse;

        ul{
            flex-direction: column;
            align-items: flex-end;
            height: 13rem;
            padding-right: 1rem;
        }

        a{
            color: black;
        }
    }
`

export const Title = styled.div`
    margin-top: 3rem;
    margin-left: 32rem;
    width: 40rem;
    border-right: solid white;
    padding-right: 3px;
    animation: blink normal infinite .9s, write normal 5s;

    @keyframes blink{
        0% {opacity: 1};
        100% {opacity: 0};
        }

    @keyframes write{
        100%{
            max-width: 100%;
        }
    }

    h1{

        width: 40rem;
        color: whitesmoke;
        
        font-size: 2.5rem;
        display: inline-block;

        text-align: center;
        text-shadow: 5px 5px 20px wheat;
        transition: 1s all;

       &:hover{
            transform: scale(1.1);
            cursor: pointer;
        }

        

        /* #tracinho::after{
            border: solid white;
            content: "|";
            color: wheat;
            margin-left: 5px;
            animation: blink 0.7s infinite;
        }

        @keyframes blink{
            0% {opacity: 1};
            100% {opacity: 0};
        } */
    }
    

    @media only screen and (min-width: 360px) and (max-width: 800px){
        width: 100%;
        margin-bottom: 10rem;
        margin-top: 0;
        margin-left: 0;
        
        h1{
            width: 100%;
            font-size: 2rem;
            line-height: 3rem;
        }
    }
`
