import styled from "styled-components";
import Computer from "../../Assets/Imagem2.webp"

export const Container = styled.section`
    border: solid red;
    background-image: url(${Computer});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    position: fixed;
    height: 100vh;
    width: 100%;

    ul{
        border: solid red;
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        width: 100%;

    }

    li{
        border: solid green;
        font-size: 1.5rem;
        list-style: none;
        
    }

    a{
        text-decoration: none;

        &:hover{
            border-bottom: 5px do;
        }
    }

    
`

export const Title = styled.h1`
    border: solid pink;
    font-size: 5rem;
    text-align: center;
    text-shadow: 5px 0px 0px pink;
    width: 100%;

`

export const Footer = styled.div`
    border: solid green;
    text-align: center;
`
