import styled from "styled-components";

export const Home = styled.div`
    border: solid black;
    width: 100%;
    
    a{
    color: black;
    font-size: 1.5rem;
    text-decoration: none;
    transition: 1s all;
    }

    &:hover{
            transform: scale(1.2);
    }
`

export const Container = styled.section`
    border: solid 5px pink;
    display: flex;

    height: 100vh;
    width: 100%;
`

export const About = styled.div`
    border: solid red;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-top: 4rem;
    width: 100%;

    h1{
        border: solid green;
        font-size: 3rem;
        margin-bottom: 3rem;
        text-align: center;
        text-shadow: 5px 0px 0px pink;
        width: 100%;
    }

    p{
        border: solid yellowgreen;
        font-family: 'Montserrat Alternates', sans-serif;
        font-size: 1.2rem;
        line-height: 1.5rem;
        margin-bottom: 1rem;
        width: 100%;
    }
`
