import styled from "styled-components";
import Lua from "../../Assets/Eunalua.1.jpg"

export const Container = styled.body`
    background-image: url(${Lua});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;

    h5{
        font-size: 1rem;
        text-align: center;
        text-shadow: 5px 5px 20px white;
        height: 2rem;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        background-position: 35%;

        h5{
            height: 4rem;
            margin-top: 1rem;
        }
    }
`

export const Box = styled.div`
    display: flex;
    flex-direction: column;
    height: 95vh;
    width: 100%;
    
    ul{
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        margin-top: 1rem;
        width: 100%;
    }

    li{
        font-size: 1.5rem;
        list-style: none;
    }

    a{
        color: whitesmoke;
        text-decoration: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column-reverse;

        ul{
            flex-direction: column;
            align-items: flex-end;
            height: 13rem;
            padding-right: 1rem;
        }

        a{
            color: black;
        }
    }
`

export const Title = styled.div`
    margin-top: 5rem;
    margin-left: 35rem;
    width: 40rem;

    p{
        width: 40rem;
        color: whitesmoke;
        font-size: 2.5rem;
        text-align: center;
        /* text-shadow: 5px 5px 20px wheat; */
        /* transition: 1s all;

       &:hover{
            transform: scale(1.1);
            cursor: pointer;
        } */
    }

    .pulse {
        animation: pulse 1.5s infinite alternate;
    }

    @keyframes pulse {
        0% {
            text-shadow: 5px 5px 20px wheat, ;
        }
        100% {
            transform: scale(1.1);
            filter: brightness(100%);
        }

        /* 0% {
            transform: scale(0.8);
            filter: brightness(50%);
        }
        100% {
            transform: scale(1.1);
            filter: brightness(100%);
        } */
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        width: 100%;
        margin-bottom: 10rem;
        margin-top: 1rem;
        margin-left: 0;
        
        p{
            width: 100%;
            font-size: 2rem;
            line-height: 3rem;
        }
    }
`
