import styled from "styled-components";
import Background from "../../Assets/Background.1.jpg"

export const Container = styled.section`
    background-image: url(${Background});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
`

export const Home = styled.div`
    height: 3rem;
    padding: 1rem;
    width: 100%;
    
    a{
        color: whitesmoke;
        text-decoration: none;
        font-size: 1.5rem;
        list-style: none;

        &:hover{
            border-bottom: 5px dotted;
            text-shadow: 5px 5px 20px wheat;
        }
    }
    
    @media only screen and (min-width: 360px) and (max-width: 800px){
        text-align: center;
    }
`
export const Box = styled.section`
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 100vh;
    width: 100%;

    h1{
        color: whitesmoke;
        font-size: 3rem;
        letter-spacing: 1rem;
        text-align: center;
        text-shadow: 5px 5px 10px wheat;
        width: 100%;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        flex-direction: column;

        h1{
            margin-top: 1rem;
            font-size: 2rem;
        }
    }
`

export const Section = styled.section`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    

    div{
        margin: 0 auto;
    }

    img{
        width: 12rem;
    }

    p{
        color: wheat;
        font-size: 1.5rem;
        text-align: center;
        width: 100%;
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){
        div{
            margin-bottom: 1rem;
        }

        img{
            width: 10rem;
        }
}
`
export const Tecnologies = styled.div`
    width: 100%;
    height: 40vh;
    display: flex;
    flex-direction: row;
    align-items: center;

    figure{
        display: flex;
        justify-content: center;
        flex-wrap: wrap;
        width: 100%;
    }

    img{
        width: 7rem;
        object-fit: contain;
        margin: 3rem;
        
    }

    .shine {
        animation: shine 1.5s infinite alternate ease-in-out;
        &:nth-child(1){
            animation-duration: 1s;
        }

        &:nth-child(2){
            animation-duration: 1.1s;
        }

        &:nth-child(3){
            animation-duration: 1.2s;
        }

        &:nth-child(4){
            animation-duration: 1.1s;
        }

        &:nth-child(5){
            animation-duration: 1s;
        }
    }

    @keyframes shine {
        0% {
            transform: scale(0.9);
        }
        100% {
            box-shadow: 0 0 30px white, 0 0 50px wheat;
            transform: scale(1);
        }
    }

    @media only screen and (min-width: 360px) and (max-width: 800px){

        img{
            width: 4.5rem;
            margin: 1rem;
        }
    }
`
